import React from "react";
import Footer from "./Footer";
import Navbar from "./Navbar";
import mainpic from "../images/Picture3.jpg";
const AboutUs = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const images = [mainpic1, mainpic2, mainpic3];

  const nextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const prevImage = () => {
    setCurrentImageIndex(
      (prevIndex) => (prevIndex - 1 + images.length) % images.length
    );
  };

  return (
    <div className="bg-gray-100 min-h-screen pb-20">
      <Navbar />
      <div className="p-4 bg-white shadow-md m-4 rounded-lg">
        <h3 className="text-2xl font-semibold mb-4">About Us</h3>
        <div className="relative mb-4">
          <img
            src={images[currentImageIndex]} // Display the current image
            alt="main-profile"
            className="w-full h-60 object-cover rounded-md"
          />
          {/* Buttons to navigate the images */}
          <button
            onClick={prevImage}
            className="absolute left-2 top-1/2 transform -translate-y-1/2 text-white bg-black bg-opacity-50 p-2 rounded-full"
          >
            &#60;
          </button>
          <button
            onClick={nextImage}
            className="absolute right-2 top-1/2 transform -translate-y-1/2 text-white bg-black bg-opacity-50 p-2 rounded-full"
          >
            &#62;
          </button>
        </div>

