import { useEffect, useState } from "react";
import { fetchTasks, deleteTask, toggleTaskCompletion } from "../api";
import { TrashIcon, CheckIcon } from "@heroicons/react/outline";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    loadTasks();
  }, []);

  const loadTasks = async () => {
    const data = await fetchTasks();
    setTasks(data);
  };

  const handleDelete = async (id) => {
    await deleteTask(id);
    setTasks(tasks.filter((task) => task._id !== id)); // Remove deleted task from state
  };

  const handleToggleComplete = async (id) => {
    try {
      const updatedTask = await toggleTaskCompletion(id);

      // ✅ Update the state immediately
      setTasks((prevTasks) =>
        prevTasks.map((task) =>
          task._id === id ? { ...task, completed: !task.completed } : task
        )
      );
    } catch (error) {
      console.error("Error toggling task:", error);
    }
  };

  return (
    <div>
      <h2 className="text-lg font-semibold mb-2 text-white">Your Tasks</h2>
      {tasks.length === 0 ? (
        <p className="text-gray-500 text-center">
          No tasks yet. Start adding some!
        </p>
      ) : (
        <div className="space-y-3">
          {tasks.map((task) => (
            <div
              key={task._id}
              className="flex items-center justify-between p-3 bg-gray-200 dark:bg-gray-700 rounded-lg shadow"
            >
              <span
                className={`text-gray-900 dark:text-white cursor-pointer ${
                  task.completed ? "line-through text-gray-500" : ""
                }`}
                onClick={() => handleToggleComplete(task._id)}
              >
                {task.title}
              </span>

              <div className="flex items-center space-x-2">
                <button
                  className="bg-green-500 hover:bg-green-600 text-white p-2 rounded-lg transition"
                  onClick={() => handleToggleComplete(task._id)}
                >
                  <CheckIcon className="w-5 h-5" />
                </button>

                <button
                  className="bg-red-500 hover:bg-red-600 text-white p-2 rounded-lg transition"
                  onClick={() => handleDelete(task._id)}
                >
                  <TrashIcon className="w-5 h-5" />
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default TaskList;
